package PackageClass10;

public class twoDimentionRays2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


	
	
	int [] []	num={ 
				{1, 2, 3},
			{199, 300, 588,700},
			{1900, 4578, 984756}
	};
	
	for(int i=0; i<num.length; i++) {
		for(int j=0; j<num[i].length; j++) {
			System.out.print(num[i][j]+" ");
		}
	System.out.println();
	} 
	
	
	}}
