package PackageClass9;

public class multiplicationTableNestedLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	

		for (int x=1; x<=12; x++) {
			for (int y=1; y<=12; y++) {
				System.out.println(x+" x "+y+" = "+(x*y));
			}  System.out.println("-------------");
			
		}
	
	
	
	}}
